export const DateConverter = (unixTime) =>{
	let t = new Date(unixTime);
	let y = t.getFullYear();
	let m = t.getMonth();
	let d = t.getDate();		
	if (d<10){
		d = '0' + d;
	}
	if (m<10){
		m = '0' + m;
	}
	return m + '/' + d + '/' + y;
}
export const ArrayFilterMain = (arr,id) => {
	const filtered = arr.filter(elem=>elem.id===id)
	return filtered[0];
}
export const ArrayFilterList = (arr,id) => {
	const filtered = arr.filter(elem=>elem.id!==id)
	return filtered;
}
