import './Brainflix.scss';
import React from 'react';
import Header from '../Header/Header'
import videoListData from '../../assets/Data/videos.json';
import mainVideoData from '../../assets/Data/video-details.json';
import VideoSection from '../VideoSection/VideoSection';
import VideoPlayer from '../VideoPlayer/VideoPlayer';
import VideoList from '../VideoList/VideoList';
import { BrowserRouter, Route, Switch} from 'react-router-dom'


class Brainflix extends React.Component{

	state = {
		video: mainVideoData,
		videoList: videoListData,
		indexNumber: 0,
		currentId: videoListData[0].id,

	}
	dateConverter = (unixTime) =>{
		let t = new Date(unixTime);
		let y = t.getFullYear();
		let m = t.getMonth();
		let d = t.getDate();		
		if (d<10){
			d = '0' + d;
		}
		if (m<10){
			m = '0' + m;
		}
		return m + '/' + d + '/' + y;
	}
	componentDidUpdate = (prevProps, prevState) => {
		if(prevProps.match.params.id !== this.props.match.params.id){
			this.setState({currentId: this.props.match.params.id})
		  window.scrollTo(0,0);
		}

	}
	arrayFilter(arr,currentId){
		arr.filter(elem=>elem.id!=currentID)
		return arr;
	}

	videoSwitcher = (id) =>{
		if(!id){
			this.setState({indexNumber: 0})
		}
		else{
			let getNum = this.state.video.findIndex(elem=>elem.id === id)
			if(getNum > -1){
				 this.setState({indexNumber: getNum})
			}
			else{
				this.setState({indexNumber: 0})
			}
		}
	}
	render = () =>{
		return(
			<>
			<Header method={this.videoSwitcher} />
			<VideoPlayer key={this.state.curentId} data={this.state.video} />
			<div className='component'>
				<VideoSection key={this.state.curentId}  data={this.arrayFilter(this.state.video,this.state.currentId)}  />
				<Route path='/:id' render ={(routerProps)=>
					<VideoList key={this.state.currentId} data={this.state.videoList} {...routerProps} />

				} />
			</div>
			</>




		);
	}
}
