import './VideoSection.scss';
import Comments from '../Comments/Comments';
import { ArrayFilterMain, DateConverter } from '../../javascriptHelper';


export default function VideoSection(props){
	const data = ArrayFilterMain(props.data,props.currentId)
	return(
		<article className='video-info'>
			<div className='video-info__wrapper'>
				<h1 className='video-info__title'>{props.data.title}</h1>
				<div className='video-info__bottom-wrapper'>
					<div className='video-info__author'>
						<h3 className='video-info__channel'>by {props.data.channel}</h3>
						<p className='video-info__date'>{props.convert(props.data.timestamp)}</p>
					</div>
					<div className='video-info__statistics'>
						<i className='video-info__views-icon'></i>
						<p className='video-info__views'>{props.data.views}</p>
						<i className='video-info__likes-icon'></i>
						<p className='video-info__likes'>{props.data.likes}</p>
					</div>
				</div>
			</div>
			<div className='video-info__description'>{props.data.description}</div>
			<Comments key={props.data.id} data={props.data.comments} convert={props.convert} />
		</article>
	);

}
