import './Brainflix.scss';
import React from 'react';
import Header from '../Header/Header'
import videoListData from '../../assets/Data/videos.json';
import mainVideoData from '../../assets/Data/video-details.json';
import VideoSection from '../VideoSection/VideoSection';
import VideoPlayer from '../VideoPlayer/VideoPlayer';
import VideoList from '../VideoList/VideoList';
import { BrowserRouter, Route, Switch} from 'react-router-dom'


class Brainflix extends React.Component{

	state = {
		video: mainVideoData,
		videoList: videoListData,
		currentId: videoListData[0].id,

	}
	dateConverter = (unixTime) =>{
		let t = new Date(unixTime);
		let y = t.getFullYear();
		let m = t.getMonth();
		let d = t.getDate();		
		if (d<10){
			d = '0' + d;
		}
		if (m<10){
			m = '0' + m;
		}
		return m + '/' + d + '/' + y;
	}
	componentDidUpdate = (prevProps) => {
		console.log(this.props);
		if(prevProps.match.params.id !== this.props.match.params.id){
			if(this.props.match.url === '/'){
				const initialId = videoListData[0].id
				this.setState({currentId: initialId})
				window.scrollTo(0,0);
			}
			else{
				this.setState({currentId: this.props.match.params.id})
				window.scrollTo(0,0);
			}
		}

	}
	arrayFilterMain = (arr,id) => {
		const filtered = arr.filter(elem=>elem.id===id)
		return filtered[0];
	}
	arrayFilterList = (arr,id) => {
		const filtered = arr.filter(elem=>elem.id!==id)
		return filtered;
	}

	render = () =>{
		return(
			<>
			<Header  />
			<VideoPlayer key={this.state.curentId} data={this.arrayFilterMain(this.state.video,this.state.currentId)} convert={this.dateConverter} />
			<div className='component'>
				<VideoSection key={this.state.curentId}  data={this.arrayFilterMain(this.state.video,this.state.currentId)} convert={this.dateConverter} />
				<Switch>
				<Route path='/:id' render ={(routerProps)=>
					<VideoList key={this.state.currentId} data={this.state.videoList} {...routerProps} />
				} />
				<Route path='/' render ={(routerProps)=>
					<VideoList key={this.state.currentId} data={this.arrayFilterList(this.state.videoList,this.state.currentId)} {...routerProps} />
				} />
				</Switch>
			</div>
			</>




		);
	}
}
			//	<VideoList key={this.state.videoList.id} vList={this.state.videoList} elemNum={this.state.indexNumber} switcher={this.videoSwitcher} />
export default Brainflix;
