class Brainflix extends React.Component{

	state = {
		video: null,
		videoList: null,
		currentId: null,

	}
	componentDidUpdate = (prevProps) => {
		if(this.state.videoList){
			console.log(this.state.videoList,'updated')

			if(UrlIdCheck(this.props.match.url,this.props.match.params.id)){
				const urlId = UrlIdConverter(this.props.match.url,this.state.videoList)
				this.setState({currentId: urlId})
				window.scrollTo(0,0);
			}
			else{
				const initialId = this.state.videoList[0].id
				this.setState({currentId: initialId})
				window.scrollTo(0,0);
			}
		}
		if(prevProps.match.params.id !== this.props.match.params.id){
			if(this.props.match.url === '/'){
				const initialId = this.videoList[0].id
				this.setState({currentId: initialId})
				window.scrollTo(0,0);
			}

			else{
				this.setState({currentId: this.props.match.params.id})
				window.scrollTo(0,0);
			}
		}
			axios.get(Url + VideoArray + this.state.currentId + '/' + ApiKey)
			.then(response =>{
				console.log(response)
				this.setState({videoList: response})
			})
	}
	componentDidMount = () =>{
		axios.get(Url + VideoArray + ApiKey)
		.then(response =>{
			this.setState({videoList: response.data})
			console.log(this.state.videoList)
		})
	}


