				<Switch>
				<Route key='uniqueKey1' path='/:id' render ={(routerProps)=>
					<VideoList key={this.state.currentId} data={this.state.videoList} currentId={this.state.currentId} {...routerProps} />
				} />
				<Route key='uniqueKey2' path='/' render ={(routerProps)=>
					<VideoList key={this.state.currentId} data={this.state.videoList} currentId={this.state.currentId} {...routerProps} />
				} />
				</Switch>
			</div>*/}
			</>
			)}
			</>
		);
	}
}
